import { Component, HostListener, Inject, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from 'src/app/_services/auth/auth.service';
import { ButtonComponent } from '@syncfusion/ej2-angular-buttons';
import { ToastComponent, ToastCloseArgs, ToastPositionModel } from '@syncfusion/ej2-angular-notifications';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {

  @ViewChild('element') public element: any;
  public position = { X: 'Right', Y: 'Bottom' };
  public toasts = [
  { title: 'Warning !', content: 'There was a problem with your network connection.', cssClass: 'e-toast-warning' },
  { title: 'Success !', content: 'Your message has been sent successfully.', cssClass: 'e-toast-success'},
  { title: 'Error !', content: 'A problem has been occurred while submitting your data.', cssClass: 'e-toast-danger' },
  { title: 'Information !', content: 'Please read the comments carefully.', cssClass: 'e-toast-info' } ];
  public toastFlag: number = 0;


  onCreate(args: any) {
    this.element.show(this.toasts[this.toastFlag]);
    ++this.toastFlag;
  }
  btnClick(args: any) {
    this.toastShow();
  }
  toastShow() {
        setTimeout(
      () => {
          this.element.show(this.toasts[this.toastFlag]);
          ++this.toastFlag;
            if (this.toastFlag === (this.toasts.length)) {
              this.toastFlag = 0;
             }
      }, 0);
  }

  form: any = {
    username: null,
    email: null,
    password: null,
  };
  isSuccessful = false;
  isSignUpFailed = false;
  errorMessage = '';


  constructor(
    private authService: AuthService,
    private _toast: ToastrService,
    private router: Router,
  ) {}

  ngOnInit(): void {}

  onSubmit(): void {
    const { username, email, password } = this.form;

    this.authService.register(username, email, password).subscribe({
      next: (data) => {
        console.log(data);
        this.isSuccessful = true;
        this.isSignUpFailed = false;
        this.showSuccess();
      },
      error: (err) => {
        this.errorMessage = err.error.message;
        this.isSignUpFailed = true;
      },
    });
    this.navigateAdmin();
  }

  public showSuccess(): void {
    this._toast.success('User Data Successfully Added');
  }

    // Redirection
    navigateAdmin() {
      this.router.navigateByUrl('/login');
    }


}
