import { Component, Inject, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { AuthService } from 'src/app/_services/auth/auth.service';
import { ButtonComponent } from '@syncfusion/ej2-angular-buttons';
import { ToastComponent, ToastCloseArgs, ToastPositionModel } from '@syncfusion/ej2-angular-notifications';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {

  form: any = {
    username: null,
    email: null,
    password: null,
  };
  isSuccessful = false;
  isSignUpFailed = false;
  errorMessage = '';

  @ViewChild('toasttype')
  private toastObj!: ToastComponent;

  @ViewChild('infoToast') 
  private btninfo!: ButtonComponent;

  @ViewChild('warningToast') 
  private btnwarn!: ButtonComponent;

  @ViewChild('errorToast') 
  private btnerr!: ButtonComponent;

  @ViewChild('successToast')
  private btnsuccess!: ButtonComponent;

  @ViewChild('hideTosat')
  private hidebtn!: ButtonComponent;

  public position: ToastPositionModel = { X: 'Right' };

  public toasts: { [key: string]: Object }[] = [
    { title: 'Warning!', content: 'There was a problem with your network connection.', cssClass: 'e-toast-warning', icon: 'e-warning toast-icons' },
    { title: 'Success!', content: 'Your message has been sent successfully.', cssClass: 'e-toast-success', icon: 'e-success toast-icons' },
    { title: 'Error!', content: 'A problem has been occurred while submitting your data.', cssClass: 'e-toast-danger', icon: 'e-error toast-icons' },
    { title: 'Information!', content: 'Please read the comments carefully.', cssClass: 'e-toast-info', icon: 'e-info toast-icons' }
];

  constructor(
    @Inject('sourceFiles') private sourceFiles: any,
    private authService: AuthService,
    private _toast: ToastrService,
    private router: Router,
  ) {
    sourceFiles.files = ['types.css'];
  }

  ngOnInit(): void {}

  onSubmit(): void {
    const { username, email, password } = this.form;

    this.authService.register(username, email, password).subscribe({
      next: (data) => {
        console.log(data);
        this.isSuccessful = true;
        this.isSignUpFailed = false;
        this.showSuccess();
      },
      error: (err) => {
        this.errorMessage = err.error.message;
        this.isSignUpFailed = true;
      },
    });
    this.navigateAdmin();
  }

  public showSuccess(): void {
    this._toast.success('User Data Successfully Added');
  }

    // Redirection
    navigateAdmin() {
      this.router.navigateByUrl('/login');
    }



    // NO

}
